import React, { useEffect, useState } from "react";
import "./Watchlist.css";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [message, setMessage] = useState("");

  const fetchWatchlist = async () => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const res = await fetch("http://localhost:5000/api/watchlist", {
        headers: {
          Authorization: token,
        },
      });

      const data = await res.json();
      if (res.ok) {
        setWatchlist(data);
        setMessage("");
      } else {
        setMessage("‚ùå Unable to fetch watchlist.");
      }
    } catch {
      setMessage("‚ùå Server error.");
    }
  };

  const removeMovie = async (id) => {
    const token = localStorage.getItem("token");
    try {
      const res = await fetch(`http://localhost:5000/api/watchlist/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: token,
        },
      });

      if (res.ok) {
        setWatchlist((prev) => prev.filter((movie) => movie._id !== id));
      } else {
        alert("‚ùå Failed to remove movie");
      }
    } catch {
      alert("‚ùå Server error");
    }
  };

  useEffect(() => {
    fetchWatchlist();
  }, []);

  return (
    <div className="watchlist-page">
      <h2>üéûÔ∏è My Watchlist</h2>
      {message && <p className="watchlist-message">{message}</p>}
      {watchlist.length === 0 ? (
        <p className="empty-msg">No movies saved yet. Add some from Search! üîç</p>
      ) : (
        <div className="watchlist-grid">
          {watchlist.map((movie) => (
            <div key={movie._id} className="watchlist-card">
              <img
                src={
                  movie.poster !== "N/A"
                    ? movie.poster
                    : "https://via.placeholder.com/150"
                }
                alt={movie.title}
              />
              <h4>{movie.title}</h4>
              <p>{movie.year}</p>
              <button onClick={() => removeMovie(movie._id)}>
                <i className="fas fa-trash-alt"></i> Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Watchlist;
