import React, { useState } from "react";
import "./SearchForm.css";

const SearchForm = () => {
  const [query, setQuery] = useState("");
  const [movies, setMovies] = useState([]);
  const [message, setMessage] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    try {
      const res = await fetch(`https://www.omdbapi.com/?apikey=cdb0adc5&s=${query}`);
      const data = await res.json();

      if (data.Response === "True") {
        setMovies(data.Search);
        setMessage("");
      } else {
        setMovies([]);
        setMessage("‚ùå No movies found.");
      }
    } catch {
      setMessage("‚ùå Error fetching movies.");
    }
  };

  const addToWatchlist = async (movie) => {
    const token = localStorage.getItem("token");
    if (!token) return alert("‚ö†Ô∏è Please log in first!");

    try {
      const res = await fetch("http://localhost:5000/api/watchlist", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: token,
        },
        body: JSON.stringify({
          imdbID: movie.imdbID,
          title: movie.Title,
          poster: movie.Poster,
          year: movie.Year,
        }),
      });

      const data = await res.json();
      if (res.ok) {
        alert("‚úÖ Added to watchlist!");
      } else {
        alert("‚ùå " + (data.error || "Failed to add."));
      }
    } catch {
      alert("‚ùå Server error.");
    }
  };

  return (
    <div className="search-page">
      <form className="search-form" onSubmit={handleSearch}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="üé• Search your favorite movies..."
        />
        <button type="submit">üîç</button>
      </form>

      {message && <p className="search-message">{message}</p>}

      <div className="movie-grid">
        {movies.map((movie) => (
          <div key={movie.imdbID} className="movie-card">
            <img
              src={
                movie.Poster !== "N/A"
                  ? movie.Poster
                  : "https://via.placeholder.com/150"
              }
              alt={movie.Title}
            />
            <h4>{movie.Title}</h4>
            <p>{movie.Year}</p>
            <button className="watchlist-btn" onClick={() => addToWatchlist(movie)}>
              <i className="fas fa-bookmark"></i> Add to Watchlist
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchForm;
